import React, { Component } from "react";
import {
    G2,
    Chart,
    Geom,
    Axis,
    Tooltip,
    Coord,
    Label,
    Legend,
    View,
    Guide,
    Shape,
    Facet,
    Util
  } from "bizcharts";
  import DataSet from "@antv/data-set";
class Report extends Component {
    render() {
        const data = [
          {
            month: "Jan",
            Tokyo: 7.0,
            London: 3.9
          },
          {
            month: "Feb",
            Tokyo: 6.9,
            London: 4.2
          },
          {
            month: "Mar",
            Tokyo: 9.5,
            London: 5.7
          },
          {
            month: "Apr",
            Tokyo: 14.5,
            London: 8.5
          },
          {
            month: "May",
            Tokyo: 18.4,
            London: 11.9
          },
          {
            month: "Jun",
            Tokyo: 21.5,
            London: 15.2
          },
          {
            month: "Jul",
            Tokyo: 25.2,
            London: 17.0
          },
          {
            month: "Aug",
            Tokyo: 26.5,
            London: 16.6
          },
          {
            month: "Sep",
            Tokyo: 23.3,
            London: 14.2
          },
          {
            month: "Oct",
            Tokyo: 18.3,
            London: 10.3
          },
          {
            month: "Nov",
            Tokyo: 13.9,
            London: 6.6
          },
          {
            month: "Dec",
            Tokyo: 9.6,
            London: 4.8
          }
        ];
        const ds = new DataSet();
        const dv = ds.createView().source(data);
        dv.transform({
          type: "fold",
          fields: ["Tokyo", "London"],
          // expend key words
          key: "city",
          value: "temperature" 
        });
        console.log(dv);
        const cols = {
          month: {
            range: [0, 1]
          }
        };
        return (
          <div>
            <Chart height={400} data={dv} scale={cols} forceFit>
              <Legend />
              <Axis name="month" />
              <Axis
                name="temperature"
                label={{
                  formatter: val => `${val}Â°C`
                }}
              />
              <Tooltip
                crosshairs={{
                  type: "y"
                }}
              />
              <Geom
                type="line"
                position="month*temperature"
                size={2}
                color={"city"}
                shape={"smooth"}
              />
              <Geom
                type="point"
                position="month*temperature"
                size={4}
                shape={"circle"}
                color={"city"}
                style={{
                  stroke: "#fff",
                  lineWidth: 1
                }}
              />
            </Chart>
          </div>
        );
      }
    }

export default Report;
