import React, { Component } from 'react';
import CreateMember from './CreateMember';
import { Table } from 'antd';
import { Modal,Button,Divider} from 'antd';
import axios from 'axios';
import CollectionUpdateForm from '../pages/UpdateMemberForm';

const confirm = Modal.confirm;
const domain = 'http://localhost:4000/members';
const token = localStorage.getItem('id_token')
const header = {'Authorization' : `Bearer ${token}`}


class Member extends Component {
    state = {
        memberList: [],
        visible: false,
    };

    showEditMoal = (record) => {
      const {form } = this.formRef.props;
      form.memberList= record
      const formFields = form.memberList;
      console.log(formFields)
      form.setFieldsValue(record)
      this.setState({ 
        visible: true,

      });
    }

    handleCancel = () => {
      this.setState({ visible: false });
    }

    handleUpdate = () => {
      const {form } = this.formRef.props;
      const formFields = form.getFieldsValue();
      console.log(formFields.membername)
      form.validateFields((err, values) => {
        if (err) {
          return;
        }
        console.log('Received values of form: ', values);
        form.resetFields();
        this.setState({ visible: false });
      });
      axios.put('http://localhost:4000/members/register',{
          membername: formFields.membername,
          room: formFields.room,
          email: formFields.email,
          tel: formFields.tel,

        }).then((data) => {
          console.log(data)
        }).catch((error) => {
          console.log(error);
          alert(error.response.data);
        })
    }

    saveFormRef = (formRef) => {
      this.formRef = formRef;
    }


    deleteMember = (_id)=> {
        axios.delete(`${domain}/`+_id,{ headers:header})
        .then((data) => {
          this.setState({
            memberList: this.state.memberList.filter(item => item._id !== _id)
          })
          console.log(data);
        }).catch((error) => {
          console.log(error);
        })
      }

      showDeleteConfirm(_id,membername){
        console.log(_id)
        confirm({
          title: `Are you sure delete ${membername}?`,
          okText: 'Yes',
          okType: 'danger',
          cancelText: 'No',
          onOk:()=>{
            this.deleteMember(_id);
            console.log('OK')
          },
          onCancel() {
            console.log('Cancel');
          },
        });
      }


    componentDidMount() {
      axios.get(domain, {headers: header})
      .then((data) => {
          this.setState({
              memberList: data.data
          });
      }).catch((error) => {
          console.log(error);
      })

    }
    // componentDidUpdate() {
    //   axios.get(domain, {headers: header})
    //   .then((data) => {
    //       this.setState({
    //           memberList: data.data
    //       });
    //   }).catch((error) => {
    //       console.log(error);
    //   })

    // }
    render(){
        const columns = [{
            title: 'Member Name',
            dataIndex: 'membername',
          }, {
            title: 'Room Number',
            dataIndex: 'room',
          }, {
            title: 'Phone Number',
            dataIndex: 'tel',
          }, {
            title: 'Email',
            dataIndex: 'email',
          },{
            title: 'Action',
            key: 'action',
            render: (record) => (
        <span>
          <Button onClick={() => this.showEditMoal(record)}>Edit</Button>
          <Divider type="vertical" />
          <Button onClick={() =>this.showDeleteConfirm(record._id,record.membername)} type="danger">Delete</Button>
        </span>
            ),
          }];

          const { memberList } = this.state;
        return(
            <CreateMember/>,
            <br/>,
            <div>
            <Table  rowKey={record => record._id} columns={columns} dataSource={memberList} />, 
            <CollectionUpdateForm
             wrappedComponentRef={this.saveFormRef}
             visible={this.state.visible}
             onCancel={this.handleCancel}
             handleSubmit={this.handleUpdate}/>
            </div>


        )
    }
}

 export default Member;
