import React from "react";
import { Descriptions } from "antd";
import axios from "axios";

const DescriptionsItem = Descriptions.Item;

class WaterBill extends React.Component {
  state = {
    memberName: "",
    meterName: "",
    month: "",
    totalMilliters: "",
    fee: ""
  };

  componentDidMount() {
    this.getWaterBill();
  }

  convertMonth() {
    const mm = "";
    var months = [
      "January",
      "February",
      "March",
      "April",
      "May",
      "June",
      "July",
      "August",
      "September",
      "October",
      "November",
      "December"
    ];

    this.month = months[mm] || "";
  }

  getWaterBill() {
    axios
      .get("http://localhost:8080/iot/meter/getWaterBill")
      .then(res => {
        this.setState({
          memberName: res.data.data[0].memberName,
          meterName: res.data.data[0].meterName,
          month: res.data.data[0].month,
          totalMilliters: res.data.data[0].totalMilliters,
          fee: res.data.data[0].fee
        });
        console.log(this.month);
        const mm = "";
        var months = [
          "January",
          "February",
          "March",
          "April",
          "May",
          "June",
          "July",
          "August",
          "September",
          "October",
          "November",
          "December"
        ];

        this.month = months[mm] || "";
        console.log(this.month);
      })
      .catch(error => {
        console.log(error);
      });
  }

  render() {
    const myDate = new Date();
    const nowmonth = myDate.getMonth() + 1;
    return (
      <div>
        <Descriptions bordered title={nowmonth} border>
          <DescriptionsItem label="Meter Name">
            {this.state.meterName}
          </DescriptionsItem>
          <DescriptionsItem label="Member Name">
            {this.state.memberName}
          </DescriptionsItem>
          <DescriptionsItem label="Month">{this.state.month}</DescriptionsItem>
          <DescriptionsItem label="Amount Fee">
            {this.state.fee}
          </DescriptionsItem>
          <DescriptionsItem label="Amount volume (totalMilliters)">
            {this.state.totalMilliters}
          </DescriptionsItem>
        </Descriptions>
      </div>
    );
  }
}

export default WaterBill;
