import React from "react";
import { Descriptions } from "antd";
import axios from "axios";

const DescriptionsItem = Descriptions.Item;

class WaterBill extends React.Component {
  state = {
    memberName: "",
    meterName: "",
    month: "",
    totalMilliters: "",
    fee: ""
  };

  componentDidMount() {
    axios
      .get("http://localhost:8080/iot/meter/getWaterBill")
      .then(res => {
        this.setState({
          memberName: res.data.memberName,
          meterName: res.data.meterName,
          month: res.data.month,
          totalMilliters: res.data.totalMilliters,
          fee: res.data.fee
        });
        console.log(res.data);
      })
      .catch(error => {
        console.log(error);
      });
  }

  render() {
    const myDate = new Date();
    const nowmonth = myDate.getMonth() + 1;
    return (
      <div>
        <Descriptions bordered title={nowmonth} border>
          <DescriptionsItem label="Meter Name">
            {this.state.meterName}
          </DescriptionsItem>
          <DescriptionsItem label="Member Name">
            {this.state.memberName}
          </DescriptionsItem>
          <DescriptionsItem label="Month">{this.state.month}</DescriptionsItem>
          <DescriptionsItem label="Amount">{this.state.fee}</DescriptionsItem>
          <DescriptionsItem label="Amount volume">
            {this.state.totalMilliters}
          </DescriptionsItem>
        </Descriptions>
      </div>
    );
  }
}

export default WaterBill;
