import React from "react";
import { Table } from "antd";
import axios from "axios";

class WaterBill extends React.Component {
  state = {
    waterBill: []
  };

  componentDidMount() {
    this.getWaterBill();
  }

  convertMonth() {
    const mm = "";
    var months = [
      "January",
      "February",
      "March",
      "April",
      "May",
      "June",
      "July",
      "August",
      "September",
      "October",
      "November",
      "December"
    ];

    this.month = months[mm] || "";
  }

  getWaterBill() {
    axios
      .get("http://localhost:8080/iot/meter/getWaterBill")
      .then(res => {
        const mm = res.data.data[0].month;
        var months = [
          "January",
          "February",
          "March",
          "April",
          "May",
          "June",
          "July",
          "August",
          "September",
          "October",
          "November",
          "December"
        ];
        const month = months[mm - 1] || "";
        console.log(month);
        this.setState({
          waterBill: res.data.data
        });
      })
      .catch(error => {
        console.log(error);
      });
  }

  render() {
    const columns = [
      {
        title: "Meter Name",
        dataIndex: "MeterName"
      },
      {
        title: "Meter Name",
        dataIndex: "MeterName"
      },
      {
        title: "Month",
        dataIndex: "month"
      },
      {
        title: "Amount Fee(Bath)",
        className: "column-money",
        dataIndex: "fee"
      },
      {
        title: "Amount volume (totalMilliters /mL)",
        dataIndex: "totalMilliters"
      }
    ];
    const { waterBill } = this.state;
    return (
      <Table
        columns={columns}
        dataSource={waterBill}
        bordered
        title={() => "Water Bill"}
      />
    );
  }
}

export default WaterBill;
