import React, { Component } from "react";
import { Card } from "antd";
import axios from "axios";
import { Chart, Geom, Axis, Tooltip, Legend } from "bizcharts";

class Report extends Component {
  state = {
    dayData: []
  };
  componentDidMount() {
    axios
      .get(
        "http://localhost:8080/iot/data/getDailyData?meterName=Sensor-1&date=2019-07-03"
      )
      .then(res => {
        this.setState({
          dayData: res.data.data
        });
        console.log(res.data.data);
      });
  }

  render() {
    const dayData = this.state;
    dayData.transform({
      type: "fold",
      field: ["totalMilliters"],
      key: "date",
      value: "totalMilliters"
    });
    console.log(dayData);
    return (
      <div>
        <Card title="Water report">
          <p
            style={{
              fontSize: 14,
              color: "rgba(0, 0, 0, 0.85)",
              marginBottom: 16,
              fontWeight: 500
            }}
          >
            Water report
          </p>

          <Card type="inner" title="Daliy report" />

          <Card type="inner" title="Weekly report" />

          <Card type="inner" title="Monthly report" />
        </Card>
      </div>
    );
  }
}

export default Report;
